INSERT INTO `db_exam` (`examid`, `exam_answer`, `exam_optiona`, `exam_optionb`, `exam_optionc`, `exam_optiond`, `exam_parse`, `exam_title`) VALUES
	(1, 'A', '每车道一辆依次交替驶入路口', '从前车右侧路肩进入路口', '向左变道穿插进入路口', '从有空隙一侧进入路口', '《实施条例》第五十三条第三款：\r\n机动车在车道减少的路口、路段，遇有前方机动车停车排队等候或者缓慢行驶的，应当每车道一辆依次交替驶入车道减少后的路口、路段。', '驾驶机动车在车道减少的路口，遇到前方车辆依次停车或缓慢行驶时怎么办？'),
	(2, 'C', '驾驶证被暂扣期间驾驶机动车的', '驾驶机动车不按规定避让校车的', '驾驶与准驾车型不符的机动车的', '驾驶机动车违反道路交通信号灯通行的', '1.不符合，2.饮酒，3.营运，4.逃逸，5.未悬挂车牌，6.伪造车牌，7.高速倒车、逆行，8.高速停车，9.载客超速20%、其他50%，10.疲劳驾驶，11.未取得校车行驶证的。这些都是扣12分。要牢记，和巧记。', '以下哪种行为机动车驾驶人将被一次记12分？'),
	(4, 'C', '核发地', '暂居地', '户籍地', '居住地', '', '机动车驾驶证遗失的，机动驾驶员应当向哪里的车辆管理所申请补发'),
	(5, 'C', '中型客车', '大型客车', '普通三轮摩托车', '牵引车', '初次申领机动车驾驶证的，可以申请准驾车型为城市公交车、大型货车、小型汽车、小型自动挡汽车、低速载货汽车、三轮汽车、残疾人专用小型自动挡载客汽车、普通三轮摩托车、普通二轮摩托车、轻便摩托车、轮式自行机械车、无轨电车、有轨电车的机动车驾驶证。', '初次申领机动车驾驶证的，可以申请下列哪种准驾车型？'),
	(6, 'D', '连续变换远、近光灯', '不断鸣喇叭', '使用远光灯', '使用近光灯，减速或停车避让', '文明驾驶，礼貌让行。交替使用远近光灯可能会使对方看不清，所以最好的是减速或者停车避让。', '夜间驾驶车辆遇自行车对向驶来时，应怎样做？'),
	(7, 'C', '主要街道', '高架路', '人行横道', '环城高速', '《中华人民共和国道路交通安全法》第四十三条：\r\n同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：\r\n（一）前车正在左转弯、掉头、超车的；\r\n（二）与对面来车有会车可能的；\r\n（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；\r\n（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '驾驶机动车行经下列哪种路段不得超车？'),
	(8, 'A', '及时减速、观察后靠右行驶让行', '保持原有车速行驶', '靠右侧加速行驶', '不让行', '', '行车中遇到后方车辆要求超车时，应怎样做？'),
	(9, 'C', '处15日以下拘留', '依法追究刑事责任', '处200以上2000元以下罚款', '吊销机动车行驶证', '《道路交通安全法》第一百条规定：驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。对驾驶前款所列机动车上道路行驶的驾驶人，处二百元以上二千元以下罚款，并吊销机动车驾驶证。', '对驾驶拼装机动车上路行驶的驾驶人，会受到下列哪种处罚？'),
	(10, 'B', '进入应急车道行驶', '尽快驶离高速公路', '在路肩低速行驶', '尽快在路边停车', '能见度只有50米，尽快驶离高速，连50米都看不到说明太危险了。\r\n（一）能见度小于２００米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时６０公里，与同车道前车保持１００米以上的距离；\r\n（二）能见度小于１００米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时４０公里，与同车道前车保持５０米以上的距离； 　　\r\n（三）能见度小于５０米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时２０公里，并从最近的出口尽快驶离高速公路。', '驾驶机动车在高速公路遇到能见度低于50米的气象条件时，车速不得超过20公里/小时，还应怎么做？'),
	(11, 'A', '判断有无变更车道的条件', '准备抢行', '迅速变更车道', '准备迅速停车', '仔细观察是为了判断是否可以变道，也就是有无变更车道的条件。如果没有变更车道的条件，应该放弃变道等候合适时机；如果有变更车道的条件，驾驶员打开转向灯后，再伺机变更车道。', '驾驶机动车向右变更车道前应仔细观察右侧车道车流情况的原因是什么？'),
	(12, 'D', '倒车灯', '前大灯', '前后雾灯', '危险报警闪光灯', '下雪天开车危险，因此要临时停车，应当开启危险报警闪光灯，提醒其他车辆注意。', '车辆在雪天临时停车时，应开启什么灯？'),
	(13, 'A', '未悬挂机动车号牌', '未携带身份证', '未携带保险合同', '未放置城市环保标志', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。 故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？'),
	(14, 'C', '继续倒车', '加速倒车', '主动停车避让', '鸣喇叭示意', '文明驾驶，礼貌让行。', '在一般道路倒车时，若发现有过往车辆通过，应怎样做？'),
	(15, 'D', '④③①②', '①②③④', '③②①④', '②③①④', '驾驶机动车在高速公路发生故障，需要停车排除故障时，应当进行如下顺序的操作：开启危险报警闪光灯；将车辆移至不妨碍交通的位置；放置警告标志，转移乘车人员至安全处，迅速报警；等待救援', '驾驶机动车在高速公路发生故障，需要停车排除故障时，以下做法先后顺序正确的是？①放置警告标志，转移乘车人员至安全处，迅速报警②开启危险报警闪光灯③将车辆移至不妨碍交通的位置④等待救援'),
	(16, 'D', '保护好现场再协商', '不要移动车辆', '疏导其他车辆绕行', '撤离现场自行协商', '《道路交通安全法》第七十条：在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不立即撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', '在道路上发生未造成人员伤亡且无争议的轻微交通事故如何处置？'),
	(17, 'A', '90日内', '6个月内', '6个月内', '60日内', '《公安部令第123号》第四十八条：\r\n机动车驾驶人应当于机动车驾驶证有效期满前九十日内，向机动车驾驶证核发地车辆管理所申请换证。', '驾驶人在驾驶证有效期满前多长时间申请换证？'),
	(18, 'C', '醉酒后驾驶机动车的', '伪造、变造机动车驾驶证的', '饮酒后驾驶机动车的', '使用伪造、变造机动车驾驶证的', '饮酒驾驶机动车辆，罚款1000元—2000元、记12分并暂扣驾照6个月；饮酒驾驶营运机动车，罚款5000元，记12分，处以15日以下拘留，并且5年内不得重新获得驾照。醉酒驾驶机动车辆，吊销驾照，5年内不得重新获取驾照，经过判决后处以拘役，并处罚金；醉酒驾驶营运机动车辆，吊销驾照，10年内不得重新获取驾照，终生不得驾驶营运车辆，经过判决后处以拘役，并处罚金。', '机动车驾驶人有以下哪种违法行为的，暂扣六个月机动车驾驶证？'),
	(19, 'A', '30公里/小时', '、40公里/小时', '50公里/小时', '60公里/小时', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 \r\n（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 \r\n（二）掉头、转弯、下陡坡时；　　 \r\n（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 \r\n（四）在冰雪、泥泞的道路上行驶时；', '驾驶机动车下陡坡、转弯、掉头时，最高速度不能超过多少？'),
	(20, 'D', '城市高架路', '城市快速路', '山区道路', '窄桥、弯道', '《道路交通安全法》第四十三条：\r\n同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：\r\n（一）前车正在左转弯、掉头、超车的；\r\n（二）与对面来车有会车可能的；\r\n（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；\r\n（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '驾驶机动车在下列哪种路段不得超车？'),
	(21, 'A', '不能变换挡位', '可以变换挡位', '可换为高挡', '停车观察', '进路口前停车观望，进了就不能换挡，如果换挡不当，就会熄火，非常危险。', '驾驶车辆驶入铁路道口前减速降挡，进入道口后应怎样做？'),
	(22, 'A', '协助保护现场，并立即报警', '立即报警，停车观望', '加速通过，不予理睬', '尽量绕道躲避', '请伸出您的援手，文明和温暖是需要传递的', '行车中遇有前方发生交通事故，需要帮助时，应怎样做？'),
	(23, 'B', '6个月', '12个月', '3个月', '2年', '《公安部令第123号》第六十四条：机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的12个月为实习期。', '机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的多长时间为实习期？'),
	(24, 'A', '协助保护现场，并立即报警', '立即报警，停车观望', '加速通过，不予理睬', '尽量绕道躲避', '请伸出您的援手，文明和温暖是需要传递的', '行车中遇有前方发生交通事故，需要帮助时，应怎样做？'),
	(25, 'A', '减速或停车让行', '鸣喇叭示意其让道', '抢在行人之前通过', '立即变道绕过行人', '如果没有人行横道，行人就有可能从任何地方走出。遇到行人一定要减速或停车让行，注意行人的安全。', '驶近没有人行横道的交叉路口时，发现有人横穿道路，应怎样做？'),
	(26, 'B', '增加驾驶人的劳动强度', '会使制动器温度升高而使制动效果急剧下降', '会缩短发动机寿命', '容易造成车辆倾翻', '持续用刹车，会造成刹车片发热，从而制动力减小，严重的会丧失制动能力，所以下长坡要用低挡，用发动机制动。', '下长坡连续使用行车制动会导致什么？'),
	(27, 'B', '左转向灯', '右转向灯', '危险报警闪光灯', '前照灯', '高速公路是左进右出，驶离高速公路时，要开启右转向灯。', '机动车驶离高速公路时，应当开启什么灯？'),
	(28, 'C', '接近没有交通信号灯控制的交叉路口时', '城市道路照明条件不良时', '与对向机动车会车时', '接近人行横道时', '记住，只有在没有路灯或者没有照明的情况下才能用远光灯，一般都用近光灯。窄路窄桥，\r\n你若用远光灯，会让对方完全看不清，掉到桥下去的。夜间驾驶机动车遇到对向来车未关闭\r\n远光灯时，变换使用远近光灯提示。', '夜间驾驶机动车在没有中心隔离设施或者没有中心线的道路上行驶，以下哪种情况下应当改用近光灯？'),
	(29, 'B', '停在交叉路口，等待安全时左转', '继续向前行驶', '在确保安全的情况下，倒车然后左转', '以上说法都不正确', '按规定，交叉路口，不可以停车，不可以倒车，只能继续向前行驶。', '当您即将通过交叉路口的时候，才意识到要左转而不是向前，以下说法正确的是什么？'),
	(30, 'A', '辅助驾乘人员保护系统', '防抱死制动系统', '驾驶人头颈保护系统', '电子制动力分配系统', '安全气囊，设置在车内前方（正副驾驶位），侧方（车内前排和后排）和车顶三个方向。装有安全气囊系统的容器外部都印有SupplementalInflatableRestraintSystem，简称SRS)的字样，直译成中文为“辅助可充气约束系统”。旨在减轻乘员的伤害程度，当发生碰撞事故时，避免乘员发生二次碰撞，或车辆发生翻滚等危险情况下被抛离座位。', '安全气囊是一种什么装置？'),
	(31, 'C', '夜间驾驶机动车在照明条件良好的路段必须使用远光灯', '夜间驾驶机动车在照明条件良好的路段可以不使用灯光', '机动车灯光一个重要的作用是提示其他机动车驾驶人和行人', '机动车灯光的作用仅仅是为了在夜间照明', '车辆的灯光不仅仅是在光线不足的情况下提供照明，而更重要的是起到警示作用，即使在白天，人们也会首先注意到亮着灯的车辆，不管是行人还是其他车辆的司机。', '关于机动车灯光的使用，以下说法正确的是什么？'),
	(32, 'A', '减速靠右行驶', '借非机动车道行驶', '紧靠路边行驶', '靠路中心行驶', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，减速靠右行驶，并与其他车辆、行人保持必要的安全距离。', '驾驶机动车在没有中心线的道路上遇相对方向来车时怎样行驶？'),
	(33, 'D', '可借道超车', '占用对面车道', '穿插等候的车辆', '依次行驶', '《道路交通安全法》第四十五条：\r\n机动车遇有前方车辆停车排队等候或者缓慢行驶时，不得借道超车或者占用对面车道，不得穿插等候的车辆。', '驾驶机动车遇到前方车辆停车排队等候或缓慢行驶时怎么办？'),
	(34, 'C', '雪天路滑，制动距离比干燥柏油路更长', '雪天能见度低，应该根据能见度控制安全距离', '能见度小于200m时，与前车至少保持50m的安全距离', '能见度小于50m时，应该驶离高速公路', '驾驶机动车在高速公路上行驶，遇低能见度气象条件时，能见度在200米以下，车速不得超过60公里/小时，与同车道前车至少保持100米的车距以保证安全。', '雪天在高速公路上驾驶时，关于安全车距错误的说法是什么？'),
	(35, 'A', '交通情况复杂，容易造成交通堵塞甚至引发事故', '交通监控设备多', '交通警察多', '车道数量少', '交叉路口车流最多，情况很复杂，如果倒车极易造车拥堵或易发事故。', '交叉路口不得倒车的原因是什么？'),
	(36, 'A', '注意避让', '若洒水车有指示箭头，在确保安全的情况下按箭头指示方向变更车道', '若洒水车无指示箭头，在确保安全的情况下选择合适的车道变更', '通过洒水车时应急加速通过', '驾驶机动车遇到前方低速行驶的洒水车作业时，需要注意避让。若洒水车有指示箭头，在确保安全的情况下按箭头指示方向变更车道；若洒水车无指示箭头，在确保安全的情况下选择合适的车道变更。不能加速通过。', '驾驶机动车遇到前方低速行驶的洒水车作业时，以下做法错误的是什么？'),
